    /**
     * Pattern used to extract Meta Data from the Manifest file.
     */
    protected static final Pattern METADATAPATTERN = Pattern.compile("^Datafeed-Manifest-Version: (.*)\\nLookup-Files: (.*)\\nData-Files: (.*)\\nTotal-Records: (.*)\\n", Pattern.MULTILINE);

    /**
     * Pattern used to extract lookup files from the Manifest file.
     */
    protected static final Pattern LOOKUPFILEPATTERN = Pattern.compile("^Lookup-File: (.*)\\nMD5-Digest: (.*)\\nFile-Size: (.*)\\n", Pattern.MULTILINE);

    /**
     * Pattern used to extract data files from the Manifest file.
     */
    protected static final Pattern DATAFILEPATTERN = Pattern.compile("^Data-File: (.*)\\nMD5-Digest: (.*)\\nFile-Size: (.*)\\nRecord-Count: (.*)\\n", Pattern.MULTILINE);

    /**
     * Calculates the MD5 hash of a file denoted by the path.
     *
     * @param path The path of the file.
     * @return The MD5 hash.
     */
    protected static final String md5(final String path) {
        try {
            MessageDigest md = MessageDigest.getInstance("MD5");
            try (DigestInputStream dis = new DigestInputStream(new FileInputStream(path), md)) {
                // Empty loop to read entire file
                while (dis.read() != -1) ;
            }

            StringBuilder sb = new StringBuilder();
            for (byte b : md.digest()) {
                sb.append(String.format("%02x", b));
            }

            return sb.toString();
        } catch (NoSuchAlgorithmException | IOException e) {
            throw new IllegalStateException("Unable to calculate MD5 of " + path);
        }
    }

